import React from 'react' 
import './TicTacToe.css'
import circle_icon from '../Assets/circle.png'
import cross_icon from '../Assets/cross.png'
import {useState} from 'react'

let data = ["","","","","","","","",""]




const TicTacToe = () => {


    let [count,setCount] = useState(0);
    let [lock,setLock] = useState(false);
    let titleRef = useRef(null);

    const toggle = (e,num) => {
        if(lock) {
            return 0;
        }
        if(count%2===0)
        {
            e.target.innerHTML = `<img src='${cross_icon}'>`;
            data[num]="x";
            setCount(++count);
        }
        else{
            e.target.innerHTML = `<img src='${circle_icon}'>`;
            data[num]="o";
            setCount(++count);

        }
        checkWin()
    }

    const checkWin = () => {
        if(data[0]===data[1] && data[1]===data[2] && data[2]!=="")
        {
            won(data);
        }

        else if(data[3]===data[4] && data[4]===data[5] && data[5]!=="")
        {
            won(data);
        }
        else if(data[6]===data[7] && data[7]===data[8] && data[8]!=="")
        {
            won(data);
        }
        else if(data[0]===data[3] && data[3]===data[6] && data[6]!=="")
        {
            won(data);
        }
        else if(data[1]===data[4] && data[4]===data[7] && data[7]!=="")
        {
            won(data);
        }
        else if(data[2]===data[5] && data[5]===data[8] && data[8]!=="")
        {
            won(data);
        }
        else if(data[0]===data[4] && data[4]===data[8] && data[8]!=="")
        {
            won(data);
        }
        else if(data[0]===data[1] && data[1]===data[2] && data[2]!=="")
        {
            won(data);
        }
        else if(data[2]===data[4] && data[4]===data[6] && data[6]!=="")
        {
            won(data);
        }








    }

    const won = (winner) => {
        setLock(true);
        if(winner==="x")
        {
            titleRef.current.innerHTML
        }
    }


  return (
    <div class='container'>
    <h1 class='title' ref={titleRef}>Tic Tac Toe Game in <span>React</span></h1>

    <div className="board">
        <div class="row1">
            <div class="boxes" onClick={(e)=>{toggle(e,0)}}>

            </div>

            <div class="boxes" onClick={(e)=>{toggle(e,1)}}>

            </div>

            <div class="boxes" onClick={(e)=>{toggle(e,2)}}>

            </div>

        </div>

        <div class="row2">
            <div class="boxes" onClick={(e)=>{toggle(e,3)}}>

            </div>

            <div class="boxes" onClick={(e)=>{toggle(e,4)}}>

            </div>

            <div class="boxes" onClick={(e)=>{toggle(e,5)}}>

            </div>

        </div> <div class="row3">
            <div class="boxes" onClick={(e)=>{toggle(e,6)}}>

            </div>

            <div class="boxes" onClick={(e)=>{toggle(e,7)}}>

            </div>

            <div class="boxes" onClick={(e)=>{toggle(e,8)}}>

            </div>

        </div>

    </div>

        <button className="reset">Reset</button>
    </div>
  )
}

export default TicTacToe
